import { Component } from '@angular/core';

@Component({
  selector: 'app-messenger-textbox',
  templateUrl: './messenger-textbox.component.html',
  styleUrls: ['./messenger-textbox.component.scss']
})
export class MessengerTextboxComponent {
  messages: string[] = [];

  constructor() { }

  sendMessage(message: string) {
    if (message.trim()){
      this.messages.push(message)
    }
  }

}
<div class="chat-container">
    <div class="messages">
      <div *ngFor="let message of messages" class="message">
        {{ message }}
      </div>
    </div>
    <form (ngSubmit)="sendMessage(newMessage.value); newMessage.value = ''" class="message-form">
      <input #newMessage type="text" placeholder="Type here...">
      <button type="submit">Send</button>
    </form>
  </div>
<p>messenger-textbox works!</p>
