import { Component } from '@angular/core';
import { faGhost } from '@fortawesome/free-solid-svg-icons';
import { NgForm } from '@angular/forms';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
faGhost = faGhost;
user = {
  username: '',
  password: ''
};
constructor(){ }
onSubmit(form: NgForm){
  if (form.valid){
    //Handle form submission here
    console.log('Form submitted:', this.user)
  }
}
}
<h1>Zip Code Instant Messenger</h1>
<h2>
<fa-icon class='ghost-Icon' [icon]="faGhost"></fa-icon>
</h2>
<form (ngSubmit)="onSubmit(loginForm)" #loginForm="ngForm">
    <div class="form-group">
      <label for="username">Username:</label>
      <input type="text" class="form-control" id="username" name="username" [(ngModel)]="user.username" required>
    </div>
    <div class="form-group">
      <label for="password">Password:</label>
      <input type="password" class="form-control" id="password" name="password" [(ngModel)]="user.password" required>
    </div>
    <button type="submit" class="btn btn-primary" [disabled]="!loginForm.valid">Submit</button>
  </form>
<p>are you sure this is the login page?</p>
