import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-create-account',
  templateUrl: './create-account.component.html',
  styleUrls: ['./create-account.component.scss']
})
export class CreateAccountComponent {

  user = {
    username: '',
    password: ''
  };

  constructor(){ }

  onSubmit(form: NgForm){
    if (form.valid){
      //handle form submission here
      console.log('Form submitted:', this.user);
    }
  }
}
<div class="account-container">
    <h1>Create Account</h1>
    <form (ngSubmit)="onSubmit(createAccountForm)" #createAccountForm="ngForm">
        <div class="form-group">
            <label for="username">Username:</label>
            <input type="text" class="form-control" id="username" name="username" [(ngModel)]="user.username" required>
          </div>
          <div class="form-group">
            <label for="password">Password:</label>
            <input type="password" class="form-control" id="password" name="password" [(ngModel)]="user.password" required>
          </div>
          <button type="submit" class="btn btn-primary" [disabled]="!createAccountForm.valid">Create Account</button>
        </form>
</div>
<p>create-account works!</p>
